package com.springbootlearning.com;

public class DAY2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Direct Conversion");
		int a=8;
		float b;
		b=a;
		System.out.println("typecasted value: "+b);
		
		System.out.println("InDirect Conversion");
		float c = 8.0f;
		int d;
		d=(int)c;
		System.out.println("typecasted value: "+d);
		}

}
